namespace Tests\UsmanMohammad\AzureStorageBlobAssets\Traits;

/**
 * AWS S3 Assets
 *
 * @author Jon Wenmoth <contact@jonnyw.me>
 */
trait FileTestTrait
{
    /**
     * Test filename
     *
     * @var string
     * @access protected
     */
    protected $filename;

    /**
     * Test directory
     *
     * @var string
     * @access protected
     */
    protected $directory;

    /**
     * Set up test environment.
     *
     * @access public
     * @return void
     * @throws \RuntimeException
     */
    public function setUp()
    {
        $this->filename  = sprintf('%s.png', uniqid());
        $this->directory = sys_get_temp_dir();

        if (!is_writable($this->directory)) {
            throw new \RuntimeException(sprintf('Test directory must be writable: %s', $this->directory));
        }

        $handle = fopen($this->getFilePath(), 'wb');
        fwrite($handle, $this->getFileContent());
        fclose($handle);
    }

    /**
     * Tear down test environment.
     *
     * @access public
     * @return void
     */
    public function tearDown()
    {
        $file = $this->getFilePath();

        if (file_exists($file)) {
            unlink($file);
        }
    }

    /**
     * Get file name.
     *
     * @access public
     * @return string
     */
    public function getFileName()
    {
        return $this->filename;
    }

    /**
     * Get file path.
     *
     * @access public
     * @return string
     */
    public function getFilePath()
    {
        return sprintf('%1$s/%2$s', $this->directory, $this->filename);
    }

    /**
     * Get file data.
     *
     * @access private
     * @return binary
     */
    private function getFileContent()
    {
        return base64_decode('');
    }
}
